//***************************************************************************//
// TripLegRepository
//***************************************************************************//
function TripLegRepository() {

    // Mock arrays
    ////
    this.tripLegs = [];

    // AddTripLeg
    this.addTripLeg = function(tripLeg) {
        this.tripLegs.push(tripLeg);
    };

    // RemoveTripLeg
    this.removeTripLeg = function(tripLeg) {
        var searchId = tripLeg.id;
        this.tripLegs = this.tripLegs.filter(function (x) {
            return x.id != searchId;
        });
        return this.tripLegs;
    };

    // GetAll
    this.getAll = function() {
        return this.tripLegs;
    };

    return this;

}




module.exports = {
    TripLegRepository: TripLegRepository
};